package com.seraph.hrms.database.dao.impl;import org.apache.commons.lang3.StringUtils;import org.hibernate.criterion.Junction;import org.hibernate.criterion.MatchMode;import org.hibernate.criterion.Order;import org.hibernate.criterion.Restrictions;import org.springframework.stereotype.Repository;import com.seraph.hrms.database.dao.DocumentDAO;import com.seraph.hrms.database.entity.Document;import com.seraph.hrms.objects.ObjectList;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   11 December 2017 */@Repositorypublic class DocumentDAOImpl		extends AbstractDAO<Document, Long> 		implements DocumentDAO {		@Override	public ObjectList<Document> findAllWithPaging(int pageNumber, int resultsPerPage, Long personnelId,			String searchKey) {		return findAllWithPagingAndOrder(pageNumber, resultsPerPage, personnelId, searchKey, null);	}	@Override	public ObjectList<Document> findAllWithPagingAndOrder(int pageNumber, int resultsPerPage, Long personnelId,			String searchKey, Order[] orders) {		final Junction conjunction = Restrictions.conjunction();		conjunction.add(Restrictions.eq("isValid", Boolean.TRUE));		conjunction.add(Restrictions.eq("personnel.id", personnelId));				if(StringUtils.isNotBlank(searchKey))		{			for(String s : searchKey.split("\\s+")) {				conjunction.add(Restrictions.disjunction()						.add(Restrictions.ilike("documentType", s, MatchMode.ANYWHERE)));			}		}				return findAllByCriterion(pageNumber, resultsPerPage, null, null, null, orders, conjunction);	}}
