package com.seraph.hrms.database.dao.impl;import java.util.List;import org.hibernate.criterion.Junction;import org.hibernate.criterion.Restrictions;import org.springframework.stereotype.Repository;import com.seraph.hrms.database.dao.PersonnelImageDAO;import com.seraph.hrms.database.entity.PersonnelImage;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   9 December 2017 */@Repositorypublic class PersonnelImageDAOImpl		extends AbstractDAO<PersonnelImage, Long> 		implements PersonnelImageDAO {	@Override	public List<PersonnelImage> findAllByPersonnelId(Long personnelId) {		final Junction conjunction = Restrictions.conjunction();		conjunction.add(Restrictions.eq("isValid", Boolean.TRUE));		conjunction.add(Restrictions.eq("personnel.id", personnelId));				return findAllByCriterionList(null, null, null, null, conjunction);	}}
