package com.seraph.hrms.database.dao.impl;import java.util.List;import org.hibernate.criterion.Junction;import org.hibernate.criterion.Order;import org.hibernate.criterion.Restrictions;import org.springframework.stereotype.Repository;import com.seraph.hrms.database.dao.SurveyResponseDAO;import com.seraph.hrms.database.entity.SurveyResponse;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   11 December 2017 */@Repositorypublic class SurveyResponseDAOImpl		extends AbstractDAO<SurveyResponse, Long> 		implements SurveyResponseDAO {	@Override	public List<SurveyResponse> findAllByPersonnelOrderedNaturally(Long personnelId) {		final Junction conjunction = Restrictions.conjunction();		conjunction.add(Restrictions.eq("isValid", Boolean.TRUE));		conjunction.add(Restrictions.eq("personnel.id", personnelId));				return findAllByCriterionList(null, null, null, new Order[] { Order.asc("surveyQuestion") }, conjunction);	}}
