package com.seraph.hrms.database.entity;import javax.persistence.Basic;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.EnumType;import javax.persistence.Enumerated;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import javax.persistence.Transient;import javax.validation.constraints.NotNull;import org.hibernate.annotations.NotFound;import org.hibernate.annotations.NotFoundAction;import org.hibernate.annotations.Where;import com.seraph.hrms.database.entity.base.BaseObject;import com.seraph.hrms.enums.Gender;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   6 December 2017 */@Entity(name = "Dependent")@Table(name = Dependent.TABLE_NAME)public class Dependent extends BaseObject {	private static final long serialVersionUID = -2387414239378608594L;		public static final String TABLE_NAME = "dependent";		private Personnel personnel;		private String lastName;		private String firstName;		private Integer age;		private Gender gender;		private String relationship;	@ManyToOne(targetEntity = Personnel.class, fetch = FetchType.LAZY)	@JoinColumn(name = "personnel_id")	@Where(clause = "valid = 1")	@NotFound(action = NotFoundAction.IGNORE)	public Personnel getPersonnel() {		return personnel;	}	public void setPersonnel(Personnel personnel) {		this.personnel = personnel;	}	@Basic	@NotNull	@Column(name = "last_name")	public String getLastName() {		return lastName;	}	public void setLastName(String lastName) {		this.lastName = lastName;	}	@Basic	@NotNull	@Column(name = "first_name")	public String getFirstName() {		return firstName;	}		@Transient	public String getFormattedName() {		return lastName + ", " + firstName;	}	public void setFirstName(String firstName) {		this.firstName = firstName;	}	@Basic	@NotNull	@Column(name = "age")	public Integer getAge() {		return age;	}	public void setAge(Integer age) {		this.age = age;	}	@Enumerated(EnumType.STRING)	@NotNull	@Column(name = "gender", length = 50)	public Gender getGender() {		return gender;	}	public void setGender(Gender gender) {		this.gender = gender;	}	@Basic	@NotNull	@Column(name = "relationship")	public String getRelationship() {		return relationship;	}	public void setRelationship(String relationship) {		this.relationship = relationship;	}}
