package com.seraph.hrms.database.entity;import javax.persistence.Basic;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import javax.validation.constraints.NotNull;import org.hibernate.annotations.NotFound;import org.hibernate.annotations.NotFoundAction;import org.hibernate.annotations.Where;import com.seraph.hrms.database.entity.base.BaseObject;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   6 December 2017 */@Entity(name = "Employment")@Table(name = Employment.TABLE_NAME)public class Employment extends BaseObject {	private static final long serialVersionUID = -6761361784365698333L;		public static final String TABLE_NAME = "employment";		private Personnel personnel;		private String companyName;		private String position;		private Float salary;		private Integer years;		private String causeOfDischarge;	@ManyToOne(targetEntity = Personnel.class, fetch = FetchType.LAZY)	@JoinColumn(name = "personnel_id")	@Where(clause = "valid = 1")	@NotFound(action = NotFoundAction.IGNORE)	public Personnel getPersonnel() {		return personnel;	}	public void setPersonnel(Personnel personnel) {		this.personnel = personnel;	}	@Basic	@NotNull	@Column(name = "company_name")	public String getCompanyName() {		return companyName;	}	public void setCompanyName(String companyName) {		this.companyName = companyName;	}	@Basic	@NotNull	@Column(name = "position")	public String getPosition() {		return position;	}	public void setPosition(String position) {		this.position = position;	}	@Basic	@NotNull	@Column(name = "salary")	public Float getSalary() {		return salary;	}	public void setSalary(Float salary) {		this.salary = salary;	}	@Basic	@NotNull	@Column(name = "years")	public Integer getYears() {		return years;	}	public void setYears(Integer years) {		this.years = years;	}	@Basic	@NotNull	@Column(name = "cause_of_discharge")	public String getCauseOfDischarge() {		return causeOfDischarge;	}	public void setCauseOfDischarge(String causeOfDischarge) {		this.causeOfDischarge = causeOfDischarge;	}}
