package com.seraph.hrms.database.entity;import javax.persistence.Basic;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import org.hibernate.annotations.NotFound;import org.hibernate.annotations.NotFoundAction;import org.hibernate.annotations.Where;import com.seraph.hrms.database.entity.base.BaseObject;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   9 December 2017 */@Entity(name = "PersonnelImage")@Table(name = PersonnelImage.TABLE_NAME)public class PersonnelImage extends BaseObject {	private static final long serialVersionUID = 5032233843514815132L;		public static final String TABLE_NAME = "personnel_image";		private Personnel personnel;		private String fileName;	@ManyToOne(targetEntity = Personnel.class, fetch = FetchType.LAZY)	@JoinColumn(name = "personnel_id")	@Where(clause = "valid = 1")	@NotFound(action = NotFoundAction.IGNORE)	public Personnel getPersonnel() {		return personnel;	}	public void setPersonnel(Personnel personnel) {		this.personnel = personnel;	}	@Basic	@Column(name = "file_name")	public String getFileName() {		return fileName;	}	public void setFileName(String fileName) {		this.fileName = fileName;	}}
