package com.seraph.hrms.database.entity;import javax.persistence.Basic;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import javax.persistence.Transient;import javax.validation.constraints.NotNull;import org.hibernate.annotations.NotFound;import org.hibernate.annotations.NotFoundAction;import org.hibernate.annotations.Where;import com.seraph.hrms.database.entity.base.BaseObject;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   6 December 2017 */@Entity(name = "Reference")@Table(name = Reference.TABLE_NAME)public class Reference extends BaseObject {	private static final long serialVersionUID = -7896407082338599169L;		public static final String TABLE_NAME = "reference";		private Personnel personnel;		private String firstName;		private String lastName;		private String contactNumber;	@ManyToOne(targetEntity = Personnel.class, fetch = FetchType.LAZY)	@JoinColumn(name = "personnel_id")	@Where(clause = "valid = 1")	@NotFound(action = NotFoundAction.IGNORE)	public Personnel getPersonnel() {		return personnel;	}	public void setPersonnel(Personnel personnel) {		this.personnel = personnel;	}	@Basic	@NotNull	@Column(name = "first_name")	public String getFirstName() {		return firstName;	}	public void setFirstName(String firstName) {		this.firstName = firstName;	}	@Basic	@NotNull	@Column(name = "last_name")	public String getLastName() {		return lastName;	}		@Transient	public String getFormattedName() {		return lastName + ", " + firstName;	}	public void setLastName(String lastName) {		this.lastName = lastName;	}	@Basic	@NotNull	@Column(name = "contact_number")	public String getContactNumber() {		return contactNumber;	}	public void setContactNumber(String contactNumber) {		this.contactNumber = contactNumber;	}}
