package com.seraph.hrms.database.service.impl;import java.util.List;import org.hibernate.criterion.Order;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.seraph.hrms.database.dao.DependentDAO;import com.seraph.hrms.database.entity.Dependent;import com.seraph.hrms.database.service.DependentService;import com.seraph.hrms.objects.ObjectList;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   6 December 2017 */@Servicepublic class DependentServiceImpl		extends AbstractService<Dependent, Long, DependentDAO> 		implements DependentService {	@Autowired	protected DependentServiceImpl(DependentDAO dao) {		super(dao);	}	@Override	public ObjectList<Dependent> findAllByPersonnelWithPagingOrderByName(int pageNumber, int resultsPerPage,			Long personnelId, String searchKey) {		return dao.findAllWithPagingAndOrder(pageNumber, resultsPerPage, personnelId, searchKey, new Order[] { Order.asc("lastName"), Order.asc("firstName") });	}	@Override	public List<Dependent> findAllByPersonnelOrderByName(Long personnelId) {		return dao.findAllByPersonnelWithOrder(personnelId, new Order[] { Order.asc("lastName"), Order.asc("firstName") });	}}
