package com.seraph.hrms.database.service.impl;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.seraph.hrms.database.dao.EmploymentDAO;import com.seraph.hrms.database.entity.Employment;import com.seraph.hrms.database.service.EmploymentService;import com.seraph.hrms.objects.ObjectList;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   6 December 2017 */@Servicepublic class EmploymentServiceImpl		extends AbstractService<Employment, Long, EmploymentDAO> 		implements EmploymentService {	@Autowired	protected EmploymentServiceImpl(EmploymentDAO dao) {		super(dao);	}		@Override	public ObjectList<Employment> findAllByPersonnelWithPaging(int pageNumber, int resultsPerPage,			Long personnelId, String searchKey) {		return dao.findAllWithPaging(pageNumber, resultsPerPage, personnelId, searchKey);	}	@Override	public List<Employment> findAllByPersonnel(Long personnelId) {		return dao.findAllByPersonnelWithOrder(personnelId, null);	}}
