package com.seraph.hrms.database.service.impl;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.seraph.hrms.database.dao.PersonnelImageDAO;import com.seraph.hrms.database.entity.PersonnelImage;import com.seraph.hrms.database.service.PersonnelImageService;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   9 December 2017 */@Servicepublic class PersonnelImageServiceImpl		extends AbstractService<PersonnelImage, Long, PersonnelImageDAO> 		implements PersonnelImageService {	@Autowired	protected PersonnelImageServiceImpl(PersonnelImageDAO dao) {		super(dao);	}	@Override	public List<PersonnelImage> findAllByPersonnelId(Long personnelId) {		return dao.findAllByPersonnelId(personnelId);	}}
