package com.seraph.hrms.database.service.impl;import java.util.List;import org.hibernate.criterion.Order;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.seraph.hrms.database.dao.ReferenceDAO;import com.seraph.hrms.database.entity.Reference;import com.seraph.hrms.database.service.ReferenceService;import com.seraph.hrms.objects.ObjectList;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   6 December 2017 */@Servicepublic class ReferenceServiceImpl		extends AbstractService<Reference, Long, ReferenceDAO> 		implements ReferenceService {	@Autowired	protected ReferenceServiceImpl(ReferenceDAO dao) {		super(dao);	}		@Override	public ObjectList<Reference> findAllByPersonnelWithPagingOrderByName(int pageNumber, int resultsPerPage,			Long personnelId, String searchKey) {		return dao.findAllWithPagingAndOrder(pageNumber, resultsPerPage, personnelId, searchKey, new Order[] { Order.asc("lastName"), Order.asc("firstName") });	}	@Override	public List<Reference> findAllByPersonnelOrderByName(Long personnelId) {		return dao.findAllByPersonnelWithOrder(personnelId, new Order[] { Order.asc("lastName"), Order.asc("firstName") });	}}
