package com.seraph.hrms.database.service.impl;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.seraph.hrms.database.dao.UserImageDAO;import com.seraph.hrms.database.entity.UserImage;import com.seraph.hrms.database.service.UserImageService;/** * @author  Adrian Jasper K. Chua * @version 1.0 * @since   6 December 2017 */@Servicepublic class UserImageServiceImpl		extends AbstractService<UserImage, Long, UserImageDAO> 		implements UserImageService {	@Autowired	protected UserImageServiceImpl(UserImageDAO dao) {		super(dao);	}	@Override	public List<UserImage> findAllByUserId(Long userId) {		return dao.findAllByUserId(userId);	}}
